export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  // Fonction pour dÃ©terminer le rÃ´le et rediriger (avec useCallback pour Ã©viter les recrÃ©ations)
  const handleUserRedirection = useCallback((userData) => {
    console.log('ðŸ” DEBUG - handleUserRedirection called with userData:', userData);
    if (!userData || !userData.role) {

      console.warn('âš ï¸ DonnÃ©es utilisateur ou rÃ´le manquant, redirection vers dashboard par dÃ©faut');
      navigate("/dashboard");
      return;
    }

    const userRole = userData.role.name;
    console.log('ðŸŽ­ RÃ´le dÃ©tectÃ©:', userRole);

    if (userRole === 'teacher') {
      console.log('ðŸ‘¨â€ðŸ« Redirection vers TeacherDashboard');
      navigate("/teacher/dashboard");
    } else {
      console.log('ðŸ‘¨â€ðŸŽ“ Redirection vers Dashboard Ã©tudiant');
      navigate("/dashboard");
    }
  }, [navigate]);

  // VÃ©rifier si un token existe au chargement de l'application
  useEffect(() => {
    const checkAuth = async () => {
      const token = getToken();
      const savedUser = getUser();
      console.log('ðŸŸ¡ checkAuth - Token exists:', !!token);
      console.log('ðŸŸ¡ checkAuth - Saved user exists:', !!savedUser);

      if (token) {
        try {
          console.log('ðŸŸ¡ Appel Ã  getMe()...');
          // Tenter de rÃ©cupÃ©rer les donnÃ©es utilisateur depuis le serveur
          const userData = await getMe();
          console.log('ðŸŸ¢ getMe() response userData:', userData);
          setUser(userData);

          // Rediriger selon le rÃ´le au chargement de l'app
          handleUserRedirection(userData);

        } catch (error) {
          console.error('ðŸ”´ Erreur dans getMe():', error);
          console.warn("Token invalide ou expirÃ©, dÃ©connexion...");
          clearAuthData();
          setUser(null);
          navigate("/login");
        }
      }  else {
        console.log('ðŸ”´ Pas de token, redirection vers login');
        navigate("/login");
      }

      setIsLoading(false);
    };

    checkAuth();
  }, [handleUserRedirection, navigate]); // âœ… Now includes the dependency

  const loginUser = (data) => {
    console.log('ðŸ” DEBUG - loginUser called with data:', data);
    if (!data || !data.jwt || !data.user) {
      console.error("DonnÃ©es d'authentification invalides");
      return;
    }

    // Sauvegarder le token et les donnÃ©es utilisateur
    saveToken(data.jwt);
    saveUser(data.user);
    setUser(data.user);

    console.log('ðŸ” DEBUG - User data to redirect:', data.user);

    // DÃ©terminer le rÃ´le et rediriger
    handleUserRedirection(data.user);
  };

  const logout = () => {
    console.log('ðŸšª DÃ©connexion');
    clearAuthData();
    setUser(null);
    navigate("/login");
  };

  return (
    <AuthContext.Provider value={{ user, loginUser, logout, isLoading }}>
      {children}
    </AuthContext.Provider>
  );
};
