import httpClient from "./httpClient";

export const login = async(email, password) =>{
    const res = await httpClient.post("/auth/local",{identifier:email, password: password});
    return res.data ;
};


export const getMe = async () =>{
    const res = await httpClient.get("/users/me");
    return res.data ;
};
export const register = async (username, email, password, roleName) => {
  // Étape 1: Créer l'utilisateur (sans le rôle, Strapi assignera automatiquement le rôle par défaut)
  const registerRes = await httpClient.post("/auth/local/register", {
    username,
    email,
    password,
  });

  // Étape 2: Récupérer tous les rôles disponibles dans Strapi
  const { data: rolesRes } = await httpClient.get("/users-permissions/roles");

  // Étape 3: Recherche du rôle choisi (student ou teacher)
  const selectedRole = rolesRes.roles.find(
    (r) => r.name.toLowerCase() === roleName.toLowerCase()
  );

  if (!selectedRole) {
    throw new Error(`Rôle "${roleName}" introuvable dans Strapi`);
  }

  // Étape 4: Mettre à jour le rôle de l'utilisateur avec le token JWT obtenu lors de l'inscription
  const token = registerRes.data.jwt;
  const userId = registerRes.data.user.id;

  // Créer un client temporaire avec le token pour mettre à jour le rôle
  const tempClient = httpClient.create ? httpClient.create() : httpClient;
  
  await tempClient.put(`/users/${userId}`, {
    role: selectedRole.id,
  }, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });

  // Étape 5: Récupérer les données utilisateur mises à jour
  const { data: updatedUser } = await tempClient.get(`/users/${userId}`, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });

  // Retourner les données avec le rôle mis à jour
  return {
    jwt: token,
    user: updatedUser,
  };
};
