import React, { useState, useContext } from "react";
import { login, register } from "../api/auth";
import { AuthContext } from "../contexts/authContext";

export default function Login() {
  const { loginUser } = useContext(AuthContext);

  
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    role: "STUDENT", 
  });

  const [isLogin, setIsLogin] = useState(true); // toggle entre login / register
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");

  // üìå Mise √† jour des champs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // üöÄ Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);

    try {
      let data;
      if (isLogin) {
        // üîπ Connexion existante
        data = await login(formData.email, formData.password);
      } else {
        // üîπ Inscription avec r√¥le s√©lectionn√©
        data = await register(
          formData.username,
          formData.email,
          formData.password,
          formData.role
        );
      }

      // ‚úÖ Sauvegarde du token + redirection
      loginUser(data);
    } catch (err) {
      console.error("Erreur backend :", err.response?.data || err.message);
      const msg =
        err.response?.data?.error?.message ||
        "‚ùå Identifiants incorrects ou serveur indisponible";
      setError(msg);
    } finally {
      setIsLoading(false);
    }
  };

  // üß© Interface utilisateur
  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 flex items-center justify-center p-4">
      <div className="max-w-md w-full relative z-10">
        <div className="bg-gray-50 rounded-2xl shadow-2xl p-8 transform transition-all duration-300 hover:shadow-xl">
          {/* En-t√™te */}
          <div className="text-center mb-8">
            <div className="w-20 h-20 bg-gradient-to-r from-orange-500 to-orange-400 rounded-full flex items-center justify-center mx-auto mb-4 shadow-lg transform hover:scale-110">
              <span className="text-2xl font-bold text-white">üß†</span>
            </div>
            <h1 className="text-3xl font-bold bg-gradient-to-r from-slate-900 to-slate-700 bg-clip-text text-transparent mb-2">
              LearnAI
            </h1>
            <p className="text-slate-600">
              {isLogin ? "Bienvenue de retour !" : "Cr√©ez votre compte"}
            </p>
          </div>

          {/* Formulaire */}
          <form onSubmit={handleSubmit} className="space-y-6">
            {!isLogin && (
              <>
                {/* Username */}
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-slate-700">
                    Nom d'utilisateur
                  </label>
                  <input
                    type="text"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    className="w-full px-4 py-3 bg-white border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 placeholder-gray-400"
                    placeholder="Ex: BassmaDev"
                    required
                  />
                </div>

                {/* S√©lecteur de r√¥le */}
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-slate-700">
                    R√¥le
                  </label>
                  <select
                    name="role"
                    value={formData.role}
                    onChange={handleChange}
                    className="w-full px-4 py-3 bg-white border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200"
                    required
                  >
                    <option value="STUDENT">√âtudiant</option>
                    <option value="PROF">Professeur</option>
                  </select>
                </div>
              </>
            )}

            {/* Email */}
            <div className="space-y-2">
              <label className="block text-sm font-semibold text-slate-700">
                Email
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="w-full px-4 py-3 bg-white border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 placeholder-gray-400"
                placeholder="votre@email.com"
                required
              />
            </div>

            {/* Mot de passe */}
            <div className="space-y-2">
              <label className="block text-sm font-semibold text-slate-700">
                Mot de passe
              </label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="w-full px-4 py-3 bg-white border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 placeholder-gray-400"
                placeholder="Votre mot de passe"
                required
              />
            </div>

            {/* Message d'erreur */}
            {error && <p className="text-red-500 text-sm text-center">{error}</p>}

            {/* Bouton principal */}
            <button
              type="submit"
              disabled={isLoading}
              className={`w-full bg-gradient-to-r from-blue-600 to-blue-500 hover:from-blue-700 hover:to-blue-600 text-white py-3 px-4 rounded-xl font-semibold transition-all duration-200 transform hover:scale-105 focus:ring-4 focus:ring-blue-200 ${
                isLoading ? "opacity-50 cursor-not-allowed" : ""
              }`}
            >
              {isLoading ? (
                <div className="flex items-center justify-center">
                  <div className="w-5 h-5 border-t-2 border-white rounded-full animate-spin mr-2"></div>
                  {isLogin ? "Connexion..." : "Cr√©ation..."}
                </div>
              ) : isLogin ? (
                "Se connecter"
              ) : (
                "Cr√©er un compte"
              )}
            </button>
          </form>

          {/* Toggle Login/Register */}
          <div className="text-center mt-6">
            <p className="text-slate-600">
              {isLogin ? "Pas de compte ?" : "D√©j√† inscrit ?"}{" "}
              <button
                onClick={() => setIsLogin(!isLogin)}
                className="text-blue-600 hover:text-blue-700 font-semibold transition-colors duration-200 hover:underline"
              >
                {isLogin ? "Cr√©er un compte" : "Se connecter"}
              </button>
            </p>
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-6">
          <p className="text-gray-400 text-sm">¬© 2025 LearnAI. Apprenez avec l'IA</p>
        </div>
      </div>
    </div>
  );
}
