import React, { useState } from 'react';

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState('accueil');

  // Donn√©es simul√©es
  const userProgress = {
    completed: 15,
    total: 20,
    percentage: 75
  };

  const recentCourses = [
    { id: 1, title: 'Introduction √† Python', progress: 75, category: 'Programmation' },
    { id: 2, title: 'Alg√®bre Lin√©aire', progress: 50, category: 'Math√©matiques' },
    { id: 3, title: 'Histoire Moderne', progress: 30, category: 'Histoire' }
  ];

  const categories = [
    { name: 'Programmation', icon: 'üíª', count: 8 },
    { name: 'Math√©matiques', icon: 'üìä', count: 5 },
    { name: 'Sciences', icon: 'üî¨', count: 4 },
    { name: 'Histoire', icon: 'üìö', count: 3 }
  ];

  const quickActions = [
    { title: 'Continuer √† lire', icon: 'üìñ', course: 'Introduction √† Python' },
    { title: 'R√©viser', icon: 'üîÑ', course: 'Alg√®bre Lin√©aire' },
    { title: 'Nouveau cours', icon: '‚ûï', action: 'explorer' }
  ];

  return (
    <div className="min-h-screen bg-slate-900">
      {/* Header */}
      <header className="bg-slate-800 border-b border-slate-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo */}
            <div className="flex items-center">
              <div className="w-8 h-8 bg-orange-500 rounded-full flex items-center justify-center mr-3">
                <span className="text-white font-bold text-sm">AI</span>
              </div>
              <h1 className="text-xl font-bold text-white">LearnAI</h1>
            </div>

