import React, { useState } from 'react';
import axios from 'axios';
import './RegistrationForm.css';

const RegistrationForm = () => {
  const [userType, setUserType] = useState('student');
  const [formData, setFormData] = useState({
    // Champs communs
    first_name: '',
    last_name: '',
    email: '',
    phone: '',
    password: '',
    cin: '',
    establishment: '',
    
    // Champs sp√©cifiques student
    birth_date: '',
    branch: '',
    cne: '',
    
    // Champs sp√©cifiques teacher
    subject: '',
    experience_years: 0
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  // Options pour les selects
  const branchOptions = [
    'Math√©matiques', 'Physique-Chimie', 'SVT', 'Philosophie', 
    'Histoire-G√©ographie', 'Fran√ßais', 'Anglais', 'Espagnol', 
    'SES', 'NSI', 'Arts'
  ];

  const subjectOptions = [
    'Math√©matiques', 'Physique-Chimie', 'SVT', 'Philosophie',
    'Histoire-G√©ographie', 'Fran√ßais', 'Anglais', 'Espagnol',
    'SES', 'NSI', 'Arts'
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const apiUrl = userType === 'student' 
        ? 'http://localhost:1337/api/students'
        : 'http://localhost:1337/api/teachers';

      // Pr√©parer les donn√©es selon le type d'utilisateur
      const submissionData = {
        data: {
          ...formData,
          is_active: true
        }
      };

      const response = await axios.post(apiUrl, submissionData);
      
      setMessage(`‚úÖ ${userType === 'student' ? '√âtudiant' : 'Enseignant'} cr√©√© avec succ√®s!`);
      resetForm();
      
    } catch (error) {
      console.error('Erreur:', error);
      setMessage('‚ùå Erreur lors de la cr√©ation du compte');
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      first_name: '',
      last_name: '',
      email: '',
      phone: '',
      password: '',
      cin: '',
      establishment: '',
      birth_date: '',
      branch: '',
      cne: '',
      subject: '',
      experience_years: 0
    });
  };

  return (
    <div className="registration-container">
      <div className="registration-form">
        <h2>Cr√©er un compte</h2>
        
        {/* S√©lection du type d'utilisateur */}
        <div className="user-type-selector">
          <button
            type="button"
            className={`type-btn ${userType === 'student' ? 'active' : ''}`}
            onClick={() => setUserType('student')}
          >
            üéì √âtudiant
          </button>
          <button
            type="button"
            className={`type-btn ${userType === 'teacher' ? 'active' : ''}`}
            onClick={() => setUserType('teacher')}
          >
            üìö Enseignant
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* Informations de base */}
          <div className="form-section">
            <h3>Informations personnelles</h3>
            <div className="form-row">
              <div className="form-group">
                <label>Pr√©nom *</label>
                <input
                  type="text"
                  name="first_name"
                  value={formData.first_name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Nom *</label>
                <input
                  type="text"
                  name="last_name"
                  value={formData.last_name}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Email *</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>T√©l√©phone</label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Mot de passe *</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>CIN</label>
                <input
                  type="text"
                  name="cin"
                  value={formData.cin}
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-group">
              <label>√âtablissement *</label>
              <input
                type="text"
                name="establishment"
                value={formData.establishment}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          {/* Champs sp√©cifiques */}
          <div className="form-section">
            <h3>Informations {userType === 'student' ? 'acad√©miques' : 'professionnelles'}</h3>
            
            {userType === 'student' ? (
              <>
                <div className="form-row">
                  <div className="form-group">
                    <label>Date de naissance</label>
                    <input
                      type="date"
                      name="birth_date"
                      value={formData.birth_date}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="form-group">
                    <label>CNE</label>
                    <input
                      type="text"
                      name="cne"
                      value={formData.cne}
                      onChange={handleInputChange}
                    />
                  </div>
                </div>
                <div className="form-group">
                  <label>Fili√®re</label>
                  <select
                    name="branch"
                    value={formData.branch}
                    onChange={handleInputChange}
                  >
                    <option value="">S√©lectionnez une fili√®re</option>
                    {branchOptions.map(branch => (
                      <option key={branch} value={branch}>{branch}</option>
                    ))}
                  </select>
                </div>
              </>
            ) : (
              <>
                <div className="form-group">
                  <label>Mati√®re enseign√©e *</label>
                  <select
                    name="subject"
                    value={formData.subject}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">S√©lectionnez une mati√®re</option>
                    {subjectOptions.map(subject => (
                      <option key={subject} value={subject}>{subject}</option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <label>Ann√©es d'exp√©rience</label>
                  <input
                    type="number"
                    name="experience_years"
                    value={formData.experience_years}
                    onChange={handleInputChange}
                    min="0"
                    max="50"
                  />
                </div>
              </>
            )}
          </div>

          {/* Message et bouton de soumission */}
          {message && (
