import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/authContext';
import { teacherService } from '../api/api';
import { getMe } from '../api/auth';

const TeacherDashboard = () => {
  const navigate = useNavigate();
  const { user, logout } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState('tableau-de-bord');
  const [courses, setCourses] = useState([]);
  const [stats, setStats] = useState({
    totalCourses: 0,
    totalStudents: 0,
    averageRating: 0,
    totalEarnings: '0 ‚Ç¨'
  });
  const [loading, setLoading] = useState(true);
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    if (user) {
      fetchTeacherData();
    }
  }, [user]);

  const fetchTeacherData = async () => {
    try {
      setLoading(true);
      
      // R√©cup√©rer le profil complet avec les donn√©es teacher
      const userProfile = await getMe();
      setProfile(userProfile);

      // R√©cup√©rer les cours du professeur
      const coursesResponse = await teacherService.getMyCourses(userProfile.id);

      // Formater les donn√©es de Strapi
      const formattedCourses = coursesResponse.data.map(course => ({
        id: course.id,
        title: course.attributes?.title || 'Titre non d√©fini',
        category: course.attributes?.category || 'Non cat√©goris√©',
        students: course.attributes?.students || 0,
        rating: course.attributes?.rating || 0,
        status: course.attributes?.published ? 'Publi√©' : 'Brouillon',
        createdDate: course.attributes?.createdAt || new Date().toISOString()
      }));

      setCourses(formattedCourses);

      // Calculer les statistiques
      const totalStudents = formattedCourses.reduce((sum, course) => sum + course.students, 0);
      const totalCourses = formattedCourses.length;
      const averageRating = formattedCourses.length > 0 
        ? formattedCourses.reduce((sum, course) => sum + course.rating, 0) / formattedCourses.length
        : 0;

      setStats({
        totalCourses,
        totalStudents,
        averageRating: averageRating.toFixed(1),
        totalEarnings: `${(totalStudents * 29.99).toFixed(0)} ‚Ç¨`
      });

    } catch (error) {
      console.error('Erreur lors du chargement des donn√©es:', error);
      alert('Erreur lors du chargement des donn√©es');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateCourse = () => {
    navigate('/teacher/add-course');
  };

  const handleEditCourse = (courseId) => {
    navigate(`/teacher/edit-course/${courseId}`);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-slate-900 flex items-center justify-center">
        <div className="text-white text-xl">Chargement de votre tableau de bord...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-900">
      {/* Header */}
      <header className="bg-slate-800 border-b border-slate-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo et Navigation */}
            <div className="flex items-center space-x-8">
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-orange-500 rounded-full flex items-center justify-center">
                  <span className="text-white font-bold text-sm">AI</span>
                </div>
                <h1 className="text-xl font-bold text-white">LearnAI</h1>
              </div>

              <nav className="hidden md:flex space-x-6">
                {['tableau-de-bord', 'mes-cours', 'ajouter-cours', 'profil'].map((tab) => (
                  <button
                    key={tab}
                    onClick={() => {
                      if (tab === 'ajouter-cours') {
                        handleCreateCourse();
                      } else if (tab === 'profil') {
                        navigate('/teacher/profile');
                      } else {
                        setActiveTab(tab);
                      }
                    }}
                    className={`capitalize px-3 py-2 rounded-lg transition-colors ${
                      activeTab === tab
                        ? 'bg-blue-600 text-white'
                        : 'text-gray-300 hover:text-white hover:bg-slate-700'
                    }`}
                  >
                    {tab.split('-').join(' ')}
                  </button>
                ))}
              </nav>
            </div>

            {/* User Menu */}
            <div className="flex items-center space-x-4">
              <span className="text-gray-300 hidden md:block">
                Bonjour, {profile?.username || user?.username}
              </span>
              <button 
                onClick={() => navigate('/teacher/profile')}
                className="text-gray-300 hover:text-white"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
              </button>
              <button 
                onClick={logout}
                className="text-gray-300 hover:text-white"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                </svg>
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* En-t√™te du Tableau de Bord */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-white mb-2">
            Tableau de Bord Enseignant
          </h2>
          <p className="text-gray-300">
            Bienvenue, {profile?.username || user?.username}! G√©rez vos cours et suivez vos statistiques.
          </p>
        </div>

        {/* Cartes de Statistiques */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-gray-100 rounded-xl p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-slate-600">Total Cours</p>
                <p className="text-2xl font-bold text-slate-900">{stats.totalCourses}</p>
              </div>
              <span className="text-2xl text-blue-600">üìö</span>
            </div>
          </div>

          <div className="bg-gray-100 rounded-xl p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-slate-600">√âtudiants</p>
                <p className="text-2xl font-bold text-slate-900">{stats.totalStudents}</p>
              </div>
              <span className="text-2xl text-green-600">üë•</span>
            </div>
          </div>

          <div className="bg-gray-100 rounded-xl p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-slate-600">Note Moyenne</p>
                <p className="text-2xl font-bold text-slate-900">{stats.averageRating}/5</p>
              </div>
              <span className="text-2xl text-orange-500">‚≠ê</span>
            </div>
          </div>

          <div className="bg-gray-100 rounded-xl p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-slate-600">Revenus</p>
                <p className="text-2xl font-bold text-slate-900">{stats.totalEarnings}</p>
              </div>
              <span className="text-2xl text-green-500">üí∞</span>
            </div>
          </div>
        </div>

        {/* Cours du Professeur */}
        <div className="bg-gray-100 rounded-xl p-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-xl font-bold text-slate-900">Mes Cours</h3>
            <button 
              onClick={handleCreateCourse}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg font-medium transition-colors"
            >
              + Nouveau Cours
            </button>
          </div>

          {courses.length === 0 ? (
            <div className="text-center py-8">
              <span className="text-6xl mb-4 block">üìö</span>
              <p className="text-slate-600 mb-4">Vous n'avez pas encore cr√©√© de cours</p>
              <button 
                onClick={handleCreateCourse}
                className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg font-medium transition-colors"
              >
                Cr√©er votre premier cours
              </button>
            </div>
          ) : (
            <div className="space-y-4">
              {courses.map((course) => (
                <div key={course.id} className="bg-white rounded-lg p-4 border border-gray-200 hover:shadow-md transition-shadow">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                        <span className="text-blue-600 text-lg">üìö</span>
                      </div>
                      <div>
                        <h4 className="font-semibold text-slate-900">{course.title}</h4>
                        <div className="flex items-center space-x-4 text-sm text-slate-600">
                          <span>{course.category}</span>
                          <span>‚Ä¢</span>
                          <span>{course.students} √©tudiants</span>
                          <span>‚Ä¢</span>
                          <span>‚≠ê {course.rating || 'N/A'}</span>
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex items-center space-x-4">
                      <span className={`px-3 py-1 rounded-full text-sm ${
                        course.status === 'Publi√©' 
                          ? 'bg-green-100 text-green-800'
                          : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {course.status}
                      </span>
                      <button 
                        onClick={() => handleEditCourse(course.id)}
                        className="text-blue-600 hover:text-blue-700 font-medium"
                      >
                        Modifier
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Navigation Mobile */}
      <div className="md:hidden fixed bottom-0 left-0 right-0 bg-slate-800 border-t border-slate-700">
        <div className="flex justify-around items-center h-16">
          {['tableau-de-bord', 'mes-cours', 'ajouter-cours', 'profil'].map((tab) => (
            <button
              key={tab}
              onClick={() => {
                if (tab === 'ajouter-cours') {
                  handleCreateCourse();
                } else if (tab === 'profil') {
                  navigate('/teacher/profile');
                } else {
                  setActiveTab(tab);
                }
              }}
              className={`flex flex-col items-center justify-center w-16 h-16 ${
                activeTab === tab ? 'text-blue-400' : 'text-gray-400'
              }`}
            >
              <span className="text-lg">
                {tab === 'tableau-de-bord' && 'üìä'}
                {tab === 'mes-cours' && 'üìö'}
                {tab === 'ajouter-cours' && '‚ûï'}
                {tab === 'profil' && 'üë§'}
              </span>
              <span className="text-xs mt-1 capitalize">{tab.split('-')[0]}</span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TeacherDashboard;
