import httpClient from "./httpClient";

export const login = async(email, password) =>{
    const res = await httpClient.post("/auth/local",{identifier:email, password: password});
    return res.data ;
};


export const getMe = async () =>{
    const res = await httpClient.get("/users/me");
    return res.data ;
};
export const register = async (username, email, password, roleName) => {
  // Récupération de tous les rôles disponibles dans Strapi
  const { data: rolesRes } = await httpClient.get("/users-permissions/roles");

  // Recherche du rôle choisi (PROF ou STUDENT)
  const selectedRole = rolesRes.roles.find(
    (r) => r.name.toLowerCase() === roleName.toLowerCase()
  );

  if (!selectedRole) throw new Error("Rôle introuvable dans Strapi");

  // Création du compte utilisateur avec le bon rôle
  const res = await httpClient.post("/auth/local/register", {
    username,
    email,
    password,
    role: selectedRole.id,
  });

  return res.data;
};
