import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, AuthContext } from './contexts/authContext';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Dashboard from './pages/Dashboard';
import HomePage from './pages/HomePage';
import CourseDetail from './pages/CourseDetail';
import Subjects from './pages/subjects';
import TeacherDashboard from './pages/TeacherDashboard';
import TeacherProfile from './pages/TeacherProfile';
import AddCourse from './pages/AddCourse';

// Component for protecting routes that require authentication
const ProtectedRoute = ({ children }) => {
  const { user, isLoading } = useContext(AuthContext);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-900 flex items-center justify-center">
        <div className="text-white text-xl">Chargement...</div>
      </div>
    );
  }

  if (!user) {
    console.log('ðŸ”´ ProtectedRoute: No user, redirecting to login');
    return <Navigate to="/login" replace />;
  }

  return children;
};

// Component for protecting teacher-only routes
const TeacherRoute = ({ children }) => {
  const { user, isLoading } = useContext(AuthContext);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-900 flex items-center justify-center">
        <div className="text-white text-xl">Chargement...</div>
      </div>
    );
  }

  if (!user) {
    console.log('ðŸ”´ TeacherRoute: No user, redirecting to login');
    return <Navigate to="/login" replace />;
  }

  if (user.role?.name !== 'teacher') {
    console.log('ðŸ”´ TeacherRoute: User is not teacher, redirecting to dashboard. Role:', user.role?.name);
    return <Navigate to="/dashboard" replace />;
  }

  return children;
};

// Component to redirect authenticated users away from auth pages
const PublicRoute = ({ children }) => {
  const { user, isLoading } = useContext(AuthContext);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-900 flex items-center justify-center">
        <div className="text-white text-xl">Chargement...</div>
      </div>
    );
  }

  if (user) {
    console.log('ðŸ”µ PublicRoute: User is authenticated, redirecting based on role:', user.role?.name);

    // Redirect teachers to teacher dashboard, students to regular dashboard
    if (user.role?.name === 'teacher') {
      return <Navigate to="/teacher/dashboard" replace />;
    } else {
      return <Navigate to="/dashboard" replace />;
    }
  }

  return children;
};

function App() {
  return (
    <Router>
      <AuthProvider>
        <div className="App">
          <Routes>
            {/* Public routes - redirect if already authenticated */}
            <Route path="/" element={
              <PublicRoute>
                <HomePage />
              </PublicRoute>
            } />
            <Route path="/login" element={
              <PublicRoute>
                <Login />
              </PublicRoute>
            } />
            <Route path="/signup" element={
              <PublicRoute>
                <Signup />
              </PublicRoute>
            } />

            {/* Protected routes - require authentication */}
            <Route path="/dashboard" element={
              <ProtectedRoute>
                <Dashboard />
              </ProtectedRoute>
            } />
            <Route path="/subjects" element={
              <ProtectedRoute>
                <Subjects />
              </ProtectedRoute>
            } />
            <Route path="/course/:courseId" element={
              <ProtectedRoute>
                <CourseDetail />
              </ProtectedRoute>
            } />

            {/* Teacher-only routes - require teacher role */}
            <Route path="/teacher/dashboard" element={
              <TeacherRoute>
                <TeacherDashboard />
              </TeacherRoute>
            } />
            <Route path="/teacher/profile" element={
              <TeacherRoute>
                <TeacherProfile />
              </TeacherRoute>
            } />
            <Route path="/teacher/add-course" element={
              <TeacherRoute>
                <AddCourse />
              </TeacherRoute>
            } />

            {/* Catch all route - redirect to home */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </div>
      </AuthProvider>
    </Router>
  );
}

export default App;
