import httpClient from "./httpClient";

import { setAuthData, clearAuthData, saveUser } from "../utils/localStorage";

/**
 * üîê Connexion utilisateur
 * @param {string} email
 * @param {string} password
 * @returns {Promise<{jwt: string, user: object}>}
 */
export const login = async (email, password) => {
  try {
    const { data } = await httpClient.post("/auth/local", {
      identifier: email,
      password,
    });
    setAuthData(data.jwt, data.user);

    return data;
  } catch (error) {
    console.error("‚ùå Erreur lors de la connexion :", error.response?.data || error.message);
    throw error;
  }
};

/**
 * üë§ R√©cup√©rer les informations de l'utilisateur connect√©
 * @returns {Promise<object>}
 */
export const getMe = async () => {
  try {
    const { data } = await httpClient.get("/users/me?populate=role");
    saveUser(data);
    return data;
  } catch (error) {
    console.error("‚ùå Erreur lors de la r√©cup√©ration du profil :", error.response?.data || error.message);
    throw error;
  }
};


export const register = async (username, email, password, roleName) => {
  try {
    console.log("Donn√©es envoy√©es :", { username, email, password });
    
    const { data: registerRes } = await httpClient.post("/auth/local/register", {
      username,
      email,
      password,
    });

    const token = registerRes.jwt;
    const userId = registerRes.user.id;

    // R√©cup√©ration du r√¥le
    const { data: rolesRes } = await httpClient.get("/users-permissions/roles");
    const selectedRole = rolesRes.roles.find(
      (r) => r.name.toLowerCase() === roleName.toLowerCase()
    );

    if (!selectedRole) throw new Error(`R√¥le "${roleName}" introuvable`);

    // Mise √† jour du r√¥le
    await httpClient.put(`/users/${userId}`, { role: selectedRole.id }, {
      headers: { Authorization: `Bearer ${token}` },
    });

    clearAuthData();

    return {
      jwt: token,
      user: registerRes.user,
    };
  } catch (error) {
    console.warn("‚ö†Ô∏è √âchec inscription :", error.response?.data || error.message);
    throw error;
  }
};
