import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './CreateLesson.css';

const CreateLesson = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    video_url: '',
    order_no: '',
    is_published: false,
    course_pdf: null
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // R√©cup√©rer l'ID du teacher connect√©
  const getTeacherId = () => {
    try {
      const token = localStorage.getItem('authToken');
      if (!token) return null;
      const decodedToken = JSON.parse(atob(token));
      return decodedToken.id;
    } catch (error) {
      console.error('Erreur d√©codage token:', error);
      return null;
    }
  };

  const userData = JSON.parse(localStorage.getItem('userData') || '{}');
  const teacherId = getTeacherId();

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFormData(prev => ({
      ...prev,
      course_pdf: file
    }));
  };

  // ‚úÖ M√âTHODE CORRECTE POUR STRAPI
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!teacherId) {
      setError('‚ùå Erreur: Impossible de r√©cup√©rer votre identifiant. Veuillez vous reconnecter.');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // ‚úÖ M√âTHODE 1: Cr√©ation sans fichier d'abord, puis upload s√©par√©
      if (!formData.course_pdf) {
        await createLessonWithoutFile();
      } else {
        await createLessonWithFile();
      }

    } catch (error) {
      console.error('‚ùå Erreur cr√©ation cours:', error);
      if (error.response) {
        console.log('D√©tails erreur:', error.response.data);
        
        // ‚úÖ Essayer la m√©thode alternative
        if (error.response.status === 400) {
          setError('Essai m√©thode alternative...');
          await tryAlternativeMethod();
        } else {
          setError(`‚ùå Erreur ${error.response.status}: ${error.response.data.error?.message || 'Erreur de validation'}`);
        }
      } else {
        setError('‚ùå Erreur de connexion au serveur');
      }
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ M√âTHODE 1A: Cr√©ation sans fichier
  const createLessonWithoutFile = async () => {
    const lessonData = {
      data: {
        title: formData.title,
        description: formData.description,
        video_url: formData.video_url,
        order_no: formData.order_no ? parseInt(formData.order_no) : null,
        is_published: formData.is_published,
        teacher: teacherId // ‚úÖ Format correct pour la relation
      }
    };

    console.log('üì§ Envoi sans fichier:', lessonData);

    const response = await axios.post('http://localhost:1337/api/lessons', lessonData);
    console.log('‚úÖ R√©ponse Strapi:', response.data);
    setSuccess('‚úÖ Cours cr√©√© avec succ√®s!');
    
    setTimeout(() => navigate('/dashboard-teacher'), 2000);
  };

  // ‚úÖ M√âTHODE 1B: Cr√©ation avec fichier
  const createLessonWithFile = async () => {
    // √âtape 1: Cr√©er le lesson d'abord
    const lessonData = {
      data: {
        title: formData.title,
        description: formData.description,
        video_url: formData.video_url,
        order_no: formData.order_no ? parseInt(formData.order_no) : null,
        is_published: formData.is_published,
        teacher: teacherId
      }
    };

    console.log('üì§ Cr√©ation lesson:', lessonData);

    const lessonResponse = await axios.post('http://localhost:1337/api/lessons', lessonData);
    const lessonId = lessonResponse.data.data.id;
    
    console.log('‚úÖ Lesson cr√©√©, ID:', lessonId);

    // √âtape 2: Upload du fichier s√©par√©ment
    await uploadPdfFile(lessonId);

    setSuccess('‚úÖ Cours cr√©√© avec fichier avec succ√®s!');
    setTimeout(() => navigate('/dashboard-teacher'), 2000);
  };

  // ‚úÖ M√âTHODE 2: Upload de fichier s√©par√©
  const uploadPdfFile = async (lessonId) => {
    const uploadFormData = new FormData();
    uploadFormData.append('files', formData.course_pdf);
    
    console.log('üì§ Upload fichier pour lesson:', lessonId);

    const uploadResponse = await axios.post('http://localhost:1337/api/lessons', uploadFormData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });

    const fileId = uploadResponse.data[0].id;
    console.log('‚úÖ Fichier upload√©, ID:', fileId);

    // √âtape 3: Associer le fichier au lesson
    const updateData = {
      data: {
        course_pdf: fileId
      }
    };

    await axios.put(`http://localhost:1337/api/lessons/${lessonId}`, updateData);
    console.log('‚úÖ Fichier associ√© au lesson');
  };

  // ‚úÖ M√âTHODE ALTERNATIVE: Tout en une seule requ√™te
  const tryAlternativeMethod = async () => {
    try {
      const formDataToSend = new FormData();
      
      // ‚úÖ Structure alternative pour Strapi
      const lessonData = {
        title: formData.title,
        description: formData.description,
        video_url: formData.video_url,
        order_no: formData.order_no ? parseInt(formData.order_no) : null,
        is_published: formData.is_published,
        teacher: teacherId
      };

      formDataToSend.append('data', JSON.stringify(lessonData));
      
      if (formData.course_pdf) {
        formDataToSend.append('files.course_pdf', formData.course_pdf);
      }

      console.log('üîÑ Essai m√©thode alternative:', lessonData);

      const response = await axios.post('http://localhost:1337/api/lessons', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('‚úÖ Succ√®s m√©thode alternative:', response.data);
      setSuccess('‚úÖ Cours cr√©√© avec succ√®s!');
      setTimeout(() => navigate('/dashboard-teacher'), 2000);

    } catch (error) {
      console.error('‚ùå √âchec m√©thode alternative:', error);
      setError('‚ùå Impossible de cr√©er le cours. V√©rifiez la configuration Strapi.');
    }
  };

  // ‚úÖ M√âTHODE SIMPLIFI√âE: Essai avec format de relation diff√©rent
  const trySimpleMethod = async () => {
    try {
      // Essayer avec un format de relation diff√©rent
      const lessonData = {
        data: {
          title: formData.title,
          description: formData.description,
          video_url: formData.video_url,
          order_no: formData.order_no ? parseInt(formData.order_no) : null,
          is_published: formData.is_published,
          teacher: {
            connect: [teacherId] // Format pour les relations
          }
        }
      };

      console.log('üîÑ Essai m√©thode simple:', lessonData);

      const response = await axios.post('http://localhost:1337/api/lessons', lessonData);
      console.log('‚úÖ Succ√®s m√©thode simple:', response.data);
      setSuccess('‚úÖ Cours cr√©√© avec succ√®s!');
      setTimeout(() => navigate('/dashboard-teacher'), 2000);

    } catch (error) {
      console.error('‚ùå √âchec m√©thode simple:', error);
      
      // Dernier essai: sans relation d'abord
      await createWithoutRelationFirst();
    }
  };

  // ‚úÖ DERNIER ESSAI: Cr√©er sans relation, puis mettre √† jour
  const createWithoutRelationFirst = async () => {
    try {
      const lessonData = {
        data: {
          title: formData.title,
          description: formData.description,
          video_url: formData.video_url,
          order_no: formData.order_no ? parseInt(formData.order_no) : null,
          is_published: formData.is_published
          // Pas de relation teacher initialement
        }
      };

      console.log('üîÑ Cr√©ation sans relation:', lessonData);

      const response = await axios.post('http://localhost:1337/api/lessons', lessonData);
      const lessonId = response.data.data.id;
      
      // Mettre √† jour avec la relation
      const updateData = {
        data: {
          teacher: teacherId
        }
      };

      await axios.put(`http://localhost:1337/api/lessons/${lessonId}`, updateData);
      
      console.log('‚úÖ Succ√®s cr√©ation + mise √† jour relation');
      setSuccess('‚úÖ Cours cr√©√© avec succ√®s!');
      setTimeout(() => navigate('/dashboard-teacher'), 2000);

    } catch (error) {
      console.error('‚ùå √âchec final:', error);
      setError('‚ùå Contactez l\'administrateur pour configurer les relations Strapi.');
    }
  };

  const handleCancel = () => {
    navigate('/dashboard-teacher');
  };

  if (!teacherId) {
    return (
      <div className="min-h-screen bg-slate-900 flex items-center justify-center">
        <div className="bg-gray-100 p-8 rounded-2xl text-center">
          <div className="text-red-500 text-6xl mb-4">‚ùå</div>
          <h2 className="text-2xl font-bold text-slate-900 mb-4">Erreur d'authentification</h2>
          <button
            onClick={() => navigate('/login')}
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-6 rounded-lg"
          >
            Se reconnecter
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-900 py-8">
      <div className="container mx-auto px-4">
        {/* Header */}
        <div className="bg-slate-800 rounded-2xl p-6 mb-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button
                onClick={handleCancel}
                className="bg-slate-700 hover:bg-slate-600 text-white p-3 rounded-lg transition-all"
              >
                ‚Üê Retour
              </button>
              <div>
                <h1 className="text-3xl font-bold text-white">Cr√©er un Nouveau Cours</h1>
                <p className="text-slate-400 mt-2">
                  Enseignant: {userData.first_name} {userData.last_name} | 
                  ID: {teacherId}
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Formulaire */}
        <div className="bg-gray-100 rounded-2xl shadow-2xl p-8">
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Champs du formulaire (inchang√©s) */}
            <div>
              <label className="block text-slate-800 font-semibold mb-3 text-lg">
                Titre du cours *
              </label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                className="w-full px-4 py-3 border-2 border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all text-slate-800"
                placeholder="Ex: Introduction aux vecteurs"
                required
              />
            </div>

            <div>
              <label className="block text-slate-800 font-semibold mb-3 text-lg">
                Description
              </label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                rows="4"
                className="w-full px-4 py-3 border-2 border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all text-slate-800 resize-vertical"
                placeholder="D√©crivez le contenu de ce cours..."
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-slate-800 font-semibold mb-3">
                  URL de la vid√©o
                </label>
                <input
                  type="url"
                  name="video_url"
                  value={formData.video_url}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border-2 border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all text-slate-800"
                  placeholder="https://..."
                />
              </div>

              <div>
                <label className="block text-slate-800 font-semibold mb-3">
                  Num√©ro d'ordre
                </label>
                <input
                  type="number"
                  name="order_no"
                  value={formData.order_no}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border-2 border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all text-slate-800"
                  placeholder="1, 2, 3..."
                  min="1"
                />
              </div>
            </div>

            {/* Fichier PDF */}
            <div>
              <label className="block text-slate-800 font-semibold mb-3 text-lg">
                Fichier PDF du cours
              </label>
              <div className="border-2 border-dashed border-slate-400 rounded-xl p-6 text-center transition-all hover:border-blue-500">
                <input
                  type="file"
                  accept=".pdf"
                  onChange={handleFileChange}
                  className="hidden"
                  id="pdf-upload"
                />
                <label htmlFor="pdf-upload" className="cursor-pointer">
                  <div className="text-slate-600 mb-2">
                    <svg className="w-12 h-12 mx-auto mb-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                    </svg>
                    <p className="text-lg font-medium">
                      {formData.course_pdf ? formData.course_pdf.name : 'Cliquez pour uploader un PDF'}
                    </p>
                  </div>
                </label>
              </div>
            </div>

            {/* Publication */}
            <div className="flex items-center space-x-3 p-4 bg-white rounded-xl border-2 border-slate-300">
              <input
                type="checkbox"
                name="is_published"
                checked={formData.is_published}
                onChange={handleInputChange}
                className="w-5 h-5 text-blue-600 rounded focus:ring-blue-500"
                id="publish-checkbox"
              />
              <label htmlFor="publish-checkbox" className="text-slate-800 font-semibold text-lg cursor-pointer">
                Publier imm√©diatement ce cours
              </label>
            </div>

            {/* Messages */}
            {error && (
              <div className="p-4 bg-red-100 border border-red-400 text-red-700 rounded-xl">
                {error}
                <button 
                  onClick={trySimpleMethod}
                  className="ml-4 bg-blue-600 text-white px-3 py-1 rounded text-sm"
                >
                  Essayer autre m√©thode
                </button>
              </div>
            )}

            {success && (
              <div className="p-4 bg-green-100 border border-green-400 text-green-700 rounded-xl">
                {success}
              </div>
            )}

            {/* Boutons */}
            <div className="flex space-x-4 pt-6">
              <button
                type="button"
                onClick={handleCancel}
                className="flex-1 bg-slate-600 hover:bg-slate-700 text-white font-semibold py-4 px-6 rounded-xl transition-all"
              >
                Annuler
              </button>
              <button
                type="submit"
                disabled={loading || !formData.title}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white font-semibold py-4 px-6 rounded-xl transition-all disabled:opacity-50"
              >
                {loading ? 'Cr√©ation...' : 'Cr√©er le Cours'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CreateLesson;
