import React, { useState } from 'react';
import axios from 'axios';
import './style/RegistrationFoem.css';

const RegistrationForm = () => {
  const [userType, setUserType] = useState('student');
  const [formData, setFormData] = useState({
    // Champs communs
    first_name: '',
    last_name: '',
    email: '',
    phone: '',
    password: '',
    cin: '',
    establishment: '',
    
    // Champs sp√©cifiques student
    birth_date: '',
    branch: '',
    cne: '',
    
    // Champs sp√©cifiques teacher
    subject: '',
    experience_years: 0
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  // ‚úÖ Enum√©rations corrig√©es selon vos sp√©cifications
  const branchOptions = ['SVT', 'PC', 'SMA'];
  const subjectOptions = ['math', 'physique', 'svt', 'arabe', 'french', 'philosophy', 'english'];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const apiUrl = userType === 'student' 
        ? 'http://localhost:1337/api/students'
        : 'http://localhost:1337/api/teachers';

      // ‚úÖ Structure correcte pour Strapi v4
      const submissionData = {
        data: {
          first_name: formData.first_name,
          last_name: formData.last_name,
          email: formData.email,
          phone: formData.phone,
          password: formData.password,
          cin: formData.cin,
          establishment: formData.establishment,
          is_active: true,
          // Champs conditionnels selon le type
          ...(userType === 'student' && {
            birth_date: formData.birth_date,
            branch: formData.branch,
            cne: formData.cne
          }),
          ...(userType === 'teacher' && {
            subject: formData.subject,
            experience_years: parseInt(formData.experience_years) || 0
          })
        }
      };

      console.log('Donn√©es envoy√©es:', submissionData);

      const response = await axios.post(apiUrl, submissionData);
      
      setMessage(`‚úÖ ${userType === 'student' ? '√âtudiant' : 'Enseignant'} cr√©√© avec succ√®s!`);
      resetForm();
      
    } catch (error) {
      console.error('Erreur d√©taill√©e:', error);
      
      if (error.response) {
        setMessage(`‚ùå Erreur ${error.response.status}: ${JSON.stringify(error.response.data.error || error.response.data)}`);
      } else if (error.request) {
        setMessage('‚ùå Pas de r√©ponse du serveur. V√©rifiez que Strapi est d√©marr√©.');
      } else {
        setMessage('‚ùå Erreur: ' + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      first_name: '',
      last_name: '',
      email: '',
      phone: '',
      password: '',
      cin: '',
      establishment: '',
      birth_date: '',
      branch: '',
      cne: '',
      subject: '',
      experience_years: 0
    });
  };

  return (
    <div className="registration-container">
      <div className="registration-form">
        <h2>Cr√©er un compte</h2>
        
        <div className="user-type-selector">
          <button
            type="button"
            className={`type-btn ${userType === 'student' ? 'active' : ''}`}
            onClick={() => setUserType('student')}
          >
            üéì √âtudiant
          </button>
          <button
            type="button"
            className={`type-btn ${userType === 'teacher' ? 'active' : ''}`}
            onClick={() => setUserType('teacher')}
          >
            üìö Enseignant
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-section">
            <h3>Informations personnelles</h3>
            <div className="form-row">
              <div className="form-group">
                <label>Pr√©nom *</label>
                <input
                  type="text"
                  name="first_name"
                  value={formData.first_name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Nom *</label>
                <input
                  type="text"
                  name="last_name"
                  value={formData.last_name}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Email *</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>T√©l√©phone</label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Mot de passe *</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>CIN</label>
                <input
                  type="text"
                  name="cin"
                  value={formData.cin}
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-group">
              <label>√âtablissement *</label>
              <input
                type="text"
                name="establishment"
                value={formData.establishment}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          {/* Champs sp√©cifiques */}
          <div className="form-section">
            <h3>Informations {userType === 'student' ? 'acad√©miques' : 'professionnelles'}</h3>
            
            {userType === 'student' ? (
              <>
                <div className="form-row">
                  <div className="form-group">
                    <label>Date de naissance</label>
                    <input
                      type="date"
                      name="birth_date"
                      value={formData.birth_date}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="form-group">
                    <label>CNE</label>
                    <input
                      type="text"
                      name="cne"
                      value={formData.cne}
                      onChange={handleInputChange}
                    />
